import React, {Component} from "react";
import "./SelectBox.css"

class SelectBox extends Component{
    constructor(props){
        super(props);
        this.state = {
            options: this.props.options,
            showOptions: false,
        }
    }

    render(){
        return(
            <div> 

                <div className= "select-box--arrow"> 
                    <span className={`${this.state.showOptions ? 'select-box-arrow--up': 'select-box--arrow-down'}`}/>
                </div>

                <div style={{display: this.state.showOptions ? "block": "none"}}></div>

                    {this.state.options.map(option => <div key={option.name}>
                        {option.value}
                </div>)
                } 
            </div>
        )

    }
}

export default SelectBox
