import React, {Component} from "react";


class Search extends Component{

    //constructor
    constructor(){ 
        super();
        this.state = {
            searched: false,
        };
        this._handleClick = this._handleClick.bind(this)
        //all event listeners should be binded in constructors
        //if not binded the event wont be able to recongnize "this"
    }

    // Methods
    _handleClick(){
        this.setState((previousState) => {// this is a method that takes a function
            return{
                searched: !previousState.searched 
            }
            }
        );
    }



    render(){ //displayes new html elements
        let searchResponse = (this.state.searched) ? "Results Page" : "Search Page";

        return(
            <div className="switch">
                <h1>{searchResponse}</h1>
                <button onClick={this._handleClick}>Search</button>
            </div>

        );

    }
}


export default Search;
