class Search extends Component{

    //constructor
    constructor(props){ 
        super(props);
        this.state = {
            searched: true, /// ummm come back to this
            resultList: null,
            inputValue: null,
            nameSelected: null,
            ingredientSelected: null, 
        };
        this._handleSearchClick = this._handleSearchClick.bind(this)
        this._handleRadioClick = this._handleRadioClick.bind(this)
        this._handleChange = this._handleChange.bind(this);  //all callbacks are invoked function style, -> context will be global context so we need to make sure we bind the context to THIS specifcally 
        //all event listeners should be binded in constructors
        //if not binded the event wont be able to recongnize "this"
    }

    // Methods
    _handleSearchClick(e){
        this.setState((previousState) => {// this.setState: a method that takes a function
            return{
                searched: !previousState.searched,
                nameSelected: true,
                ingredientSelected: true
            }
        });
        // console.log(e) <- interesting
    }

    _handleRadioClick(){
        this.setState((previousState) => {
            return{
                nameSelected: !previousState.nameSelected,
            }
        });
        console.log(this.state.nameSelected)
    }


