        }
    }
*/

    _handleChange(e){ //react automatically passes event in e 
        this.setState( // bracket for "defining function" parentheses invoke something 
           {inputValue: e.target.value}
        );
        this.props.inputProp(this.state.inputValue) //handlechange calls function defined in props
    }

    filterName(){
        if(this.state.nameSelected && this.state.searched){ //Have to pass other componenet state as props
            //const resLis = results.filter(x => x.title.toLowerCase().includes(this.state.inputValue))
            //this.setState({data: results})
        }
    }

    //display search is a function that console.logs (test)
    displaySearch = (propTest) =>{
        if(this.state.searched){
            console.log(propTest) // BY HAVING PROPS THIS IS ACTUALLY HOW IM ABLE TO GET THE DIRECT VALUE FROM MY OTHER COMPONENT
            //console.log(resultComponent[0].props.productInfo.title)
        }
    }

    render(){ //displayes new html elements
        const resultList = this.state.data

        //when rendering {Text} your assinging it a prop - things that get passed down to it
        //giving it the prop on change
        return(
            <div className="searchCriteria">

                <input type = "text" value = {this.state.inputValue} placeholder = "Search For..." onChange = {this._handleChange}></input>
                <input type = "radio" value = {this.state.nameSelected} name="categories" id="Name" onClick = {this._handleRadioClick}></input>
                    <label htmlFor="Name">Name</label>
                <input type = "radio" value = {this.state.ingredientSelected} name="categories" id="Ingredient" onClick = {this._handleSearchClick}></input>
                    <label htmlFor="Ingredient">Ingredient</label>

                <button onClick={this._handleClick}>Search!</button>
            </div>

        );
    }

}


export default Search;
